export const generateSecret = async () => {
	const secret = speakeasy.generateSecret();

	return {
		otpUrl: secret.otpauth_url,
		secret: secret.base32,
	};
};

export const secretToOtpAuthUrl = (secret: string, username: string) => {
	const url = new URL(`otpauth://totp/${secret}?secret=${secret}&issuer=My%20Profiles - ${username}`);

	return url.toString();
};

export const urlToQrCode = async (url: string) => {
	const qrCodeUrl = await QRCode.toDataURL(url.toString());

	return qrCodeUrl;
};

// Verify a token from users authenticator app or from generateToken().
export const verifyToken = async (secret: string, token: string) => {
	const verified = speakeasy.totp.verify({
		secret,
		encoding: 'base32',
		token,
	});

	return verified;
};

// Generate a token from their secret. Used for SMS or Email 2FA.
export const generateToken = async (secret: string) => {
	const token = speakeasy.totp({
		secret,
		encoding: 'base32',
	});

	return token;
};

// Fetchable pattern used for all requests
type Fetchable<T> = {
  isFetched: boolean;
  isLoading: boolean;
  data: null | T;
  error: null | string;
};

// Spawn default state for fetchable when needed
const FETCHABLE = () => ({
  isFetched: false,
  isLoading: false,
  data: null,
  error: null,
});

// Hook for typing fetchable
const useFetchable = <T,>() => useState<Fetchable<T>>(FETCHABLE());

// Accept a promise to run and auto set fetchable isLoading and error states.
const handleFetchable = async (
  action: () => Promise<any>,
  setFetchable: (fn: (fetchable: Fetchable<any>) => Fetchable<any>) => void
) => {
  setFetchable((prev: Fetchable<any>) => ({
    ...prev,
    isLoading: true,
  }));

  try {
    const data = await action();

    setFetchable((prev: Fetchable<any>) => ({
      ...prev,
      data,
    }));
  } catch (error) {
    console.error(error);

    setFetchable((prev: Fetchable<any>) => ({
      ...prev,
      error: String(error),
    }));
  } finally {
    setFetchable((prev: Fetchable<any>) => ({
      ...prev,
      isLoading: false,
      isFetched: true,
    }));
  }
};

